import HeaderComponent from "../../components/header/Header";
import React, { useState } from "react";

import "./Projects.scss";

function Projects() {
  const questions = [
    {
      question: "Qual a cor favorita do Otávio?",
      options: ["Azul", "Verde", "Vermelho", "Amarelo"],
      answer: "Azul",
    },
    {
      question: "A primeira cartinha que o Otavio fez foi:",
      options: ["Do Harry Potter", "Do Senhor dos Anéis", "De Star Wars", "De Pokémon"],
      answer: "Do Harry Potter",
    },
    {
      question: "Qual o time do Otávio?",
      options: ["Grêmio", "Inter", "Santos", "Corinthians"],
      answer: "Grêmio",
    },
    {
      question: "Qual é o tipo de filme favorito do Otávio?",
      options: ["Terror", "Comédia", "Ação", "Romance"],
      answer: "Terror",
    },
    {
      question: "Qual série o Otávio perdeu a aposta dos beijos?",
      options: ["Boba Fett", "Breaking Bad", "Stranger Things", "Game of Thrones"],
      answer: "Boba Fett",
    },
  ];

  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);

  const handleAnswer = (selectedOption) => {
    if (selectedOption === questions[currentQuestion].answer) {
      setScore(score + 1);
    }

    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
    }
  };

  return (
    <div className="app">
      <HeaderComponent />
      {currentQuestion < questions.length ? (
        <div>
          <h1>Conhece mesmo o Otávio?</h1>
          <p>{questions[currentQuestion].question}</p>
          <ul className="perguntas">
            {questions[currentQuestion].options.map((option, index) => (
              <li className="perguntas" key={index}>
                <button className="button" onClick={() => handleAnswer(option)}>{option}</button>
              </li>
            ))}
          </ul>
        </div>
      ) : (
        <div>
          <h2>Quiz completed!</h2>
          <p>Your score: {score} out of {questions.length}</p>
        </div>
      )}
    </div>
  );
}

export default Projects;
