import HeaderComponent from "../../components/header/Header";
import React, { useState, useEffect } from "react";

import "./Projects.scss";

function Projects() {
  const questions = [
    {
      question: "Qual a cor favorita do Otávio?",
      options: ["Azul", "Verde", "Vermelho", "Amarelo"],
      answer: "Azul",
    },
    {
      question: "A primeira cartinha que o Otavio fez foi:",
      options: [
        "Do Harry Potter",
        "Do Senhor dos Anéis",
        "De Star Wars",
        "De Pokémon",
      ],
      answer: "Do Harry Potter",
    },
    {
      question: "Qual o time do Otávio?",
      options: ["Grêmio", "Inter", "Santos", "Corinthians"],
      answer: "Grêmio",
    },
    {
      question: "Qual é o tipo de filme favorito do Otávio?",
      options: ["Comédia", "Ação", "Romance", "Terror"],
      answer: "Terror",
    },
    {
      question: "Qual série o Otávio perdeu a aposta dos beijos?",
      options: [
        "Boba Fett",
        "Breaking Bad",
        "Stranger Things",
        "Game of Thrones",
      ],
      answer: "Boba Fett",
    },
  ];

  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const [quizCompleted, setQuizCompleted] = useState(false);

  // Função para embaralhar as opções de resposta
  const shuffleOptions = (options) => {
    for (let i = options.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [options[i], options[j]] = [options[j], options[i]];
    }
    return options;
  };

  useEffect(() => {
    if (currentQuestion < questions.length && !quizCompleted) {
      questions[currentQuestion].options = shuffleOptions(questions[currentQuestion].options);
    }
  }, [currentQuestion]);

  const handleAnswer = (selectedOption) => {
    if (selectedOption === questions[currentQuestion].answer) {
      setScore(score + 1);
    }

    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      setQuizCompleted(true);
    }
  };

  return (
    <div className="app">
      <HeaderComponent />
      <div className="content">
        {quizCompleted ? (
          <div>
            <h1>Quiz completed!</h1>
            <p className="perguntas">
              Parabens! Você acertou: {score} de {questions.length}
            </p>
          </div>
        ) : (
          <div>
            <h1>Conhece mesmo o Otávio?</h1>
            <p className="questions">{questions[currentQuestion].question}</p>
            <ul className="perguntas">
              {questions[currentQuestion].options.map((option, index) => (
                <li className="perguntas" key={index}>
                  <button
                    className="button"
                    onClick={() => handleAnswer(option)}
                  >
                    {option}
                  </button>
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
}

export default Projects;
